-- Season Table
IF OBJECT_ID('dbo.Season', 'U') IS NOT NULL DROP TABLE dbo.Season;
CREATE TABLE dbo.Season
(
    season_id INT IDENTITY(1,1) PRIMARY KEY,
    year INT NOT NULL UNIQUE,
    status VARCHAR(20) DEFAULT 'active' CHECK (status IN ('planned', 'active', 'completed')),
    created_at DATETIME2 DEFAULT GETDATE(),
    updated_at DATETIME2 DEFAULT GETDATE()
);
GO

-- Country Table (New for 3NF)
IF OBJECT_ID('dbo.Country', 'U') IS NOT NULL DROP TABLE dbo.Country;
CREATE TABLE dbo.Country
(
    country_code VARCHAR(3) NOT NULL PRIMARY KEY, -- e.g. GBR, USA
    name VARCHAR(80) NOT NULL,
    flag_image VARBINARY(MAX) NULL, -- Storing Flag Image BLOB
    created_at DATETIME2 DEFAULT GETDATE()
);
GO

-- Circuit Table
IF OBJECT_ID('dbo.Circuit', 'U') IS NOT NULL DROP TABLE dbo.Circuit;
CREATE TABLE dbo.Circuit
(
    circuit_id INT IDENTITY(1,1) PRIMARY KEY,
    circuit_key INT UNIQUE NULL,
    name VARCHAR(120) NOT NULL,
    city VARCHAR(120) NULL,
    -- Removed country_name to satisfy 3NF (transitive dependency via country_code)
    country_code VARCHAR(3) NULL FOREIGN KEY REFERENCES Country(country_code),
    latitude DECIMAL(9,6) NULL,
    longitude DECIMAL(9,6) NULL,
    length_km DECIMAL(5,3) NULL,
    turns INT NULL,
    altitude_m INT NULL,
    created_at DATETIME2 DEFAULT GETDATE(),
    updated_at DATETIME2 DEFAULT GETDATE()
);
GO

