IF OBJECT_ID('dbo.Event', 'U') IS NOT NULL DROP TABLE dbo.Event;
CREATE TABLE dbo.Event
(
    event_id INT IDENTITY(1,1) PRIMARY KEY,
    meeting_key INT NOT NULL UNIQUE,
    season_id INT NOT NULL FOREIGN KEY REFERENCES Season(season_id),
    round_no INT NULL,
    gp_name VARCHAR(120) NOT NULL,
    meeting_official_name VARCHAR(200) NULL,
    -- Removed country_name to satisfy 3NF
    country_code VARCHAR(3) NULL FOREIGN KEY REFERENCES Country(country_code),
    location VARCHAR(120) NULL,
    circuit_key INT NULL,
    circuit_short_name VARCHAR(120) NULL,
    gmt_offset VARCHAR(16) NULL,
    date_start DATETIME2 NOT NULL,
    date_end DATETIME2 NULL,
    created_at DATETIME2 DEFAULT GETDATE(),
    updated_at DATETIME2 DEFAULT GETDATE(),
    INDEX IX_Event_Season (season_id),
    INDEX IX_Event_MeetingKey (meeting_key)
);
