-- View: Complete Event Schedule
IF OBJECT_ID('dbo.vw_EventSchedule', 'V') IS NOT NULL DROP VIEW dbo.vw_EventSchedule;
GO
CREATE VIEW dbo.vw_EventSchedule AS
SELECT 
    s.year,
    e.round_no,
    e.gp_name,
    e.country_name,
    e.location,
    e.circuit_short_name,
    e.date_start,
    e.date_end,
    COUNT(DISTINCT ses.session_id) AS session_count
FROM Event e
INNER JOIN Season s ON e.season_id = s.season_id
LEFT JOIN Session ses ON e.event_id = ses.event_id
GROUP BY s.year, e.round_no, e.gp_name, e.country_name, e.location, 
         e.circuit_short_name, e.date_start, e.date_end;
GO

-- View: Driver Championship Standings
IF OBJECT_ID('dbo.vw_DriverStandings', 'V') IS NOT NULL DROP VIEW dbo.vw_DriverStandings;
GO
CREATE VIEW dbo.vw_DriverStandings AS
WITH RaceResults AS (
    SELECT 
        s.year,
        sr.driver_id,
        d.full_name,
        d.code,
        sr.position,
        CASE 
            WHEN sr.position = 1 THEN 25
            WHEN sr.position = 2 THEN 18
            WHEN sr.position = 3 THEN 15
            WHEN sr.position = 4 THEN 12
            WHEN sr.position = 5 THEN 10
            WHEN sr.position = 6 THEN 8
            WHEN sr.position = 7 THEN 6
            WHEN sr.position = 8 THEN 4
            WHEN sr.position = 9 THEN 2
            WHEN sr.position = 10 THEN 1
            ELSE 0
        END AS points,
        CASE WHEN sr.position = 1 THEN 1 ELSE 0 END AS wins,
        CASE WHEN sr.position <= 3 THEN 1 ELSE 0 END AS podiums
    FROM SessionResult sr
    INNER JOIN Session s ON sr.session_id = s.session_id
    INNER JOIN Driver d ON sr.driver_id = d.driver_id
    WHERE s.session_type = 'Race' AND sr.dnf = 0
)
SELECT 
    year,
    driver_id,
    full_name,
    code,
    SUM(points) AS total_points,
    SUM(wins) AS total_wins,
    SUM(podiums) AS total_podiums,
    COUNT(*) AS races_completed,
    RANK() OVER (PARTITION BY year ORDER BY SUM(points) DESC) AS championship_position
FROM RaceResults
GROUP BY year, driver_id, full_name, code;
GO

-- View: Team Championship Standings
IF OBJECT_ID('dbo.vw_TeamStandings', 'V') IS NOT NULL DROP VIEW dbo.vw_TeamStandings;
GO
CREATE VIEW dbo.vw_TeamStandings AS
WITH RaceResults AS (
    SELECT 
        s.year,
        c.team_id,
        t.name AS team_name,
        sr.position,
        CASE 
            WHEN sr.position = 1 THEN 25
            WHEN sr.position = 2 THEN 18
            WHEN sr.position = 3 THEN 15
            WHEN sr.position = 4 THEN 12
            WHEN sr.position = 5 THEN 10
            WHEN sr.position = 6 THEN 8
            WHEN sr.position = 7 THEN 6
            WHEN sr.position = 8 THEN 4
            WHEN sr.position = 9 THEN 2
            WHEN sr.position = 10 THEN 1
            ELSE 0
        END AS points,
        CASE WHEN sr.position = 1 THEN 1 ELSE 0 END AS wins,
        CASE WHEN sr.position <= 3 THEN 1 ELSE 0 END AS podiums
    FROM SessionResult sr
    INNER JOIN Session s ON sr.session_id = s.session_id
    INNER JOIN Driver d ON sr.driver_id = d.driver_id
    INNER JOIN Contract c ON d.driver_id = c.driver_id AND s.year = (SELECT year FROM Season WHERE season_id = c.season_id)
    INNER JOIN Team t ON c.team_id = t.team_id
    WHERE s.session_type = 'Race' AND sr.dnf = 0
)
SELECT 
    year,
    team_id,
    team_name,
    SUM(points) AS total_points,
    SUM(wins) AS total_wins,
    SUM(podiums) AS total_podiums,
    COUNT(*) AS races_completed,
    RANK() OVER (PARTITION BY year ORDER BY SUM(points) DESC) AS championship_position
FROM RaceResults
GROUP BY year, team_id, team_name;
