-- View: Race Results
IF OBJECT_ID('dbo.vw_RaceResults', 'V') IS NOT NULL 
    DROP VIEW dbo.vw_RaceResults;
GO

CREATE VIEW dbo.vw_RaceResults AS
SELECT 
    e.gp_name,
    e.date_start,
    sr.position,
    d.full_name AS driver_name,
    d.code AS driver_code,
    t.name AS team_name,
    sr.number_of_laps,
    sr.gap_to_leader,
    CASE 
        WHEN sr.dnf = 1 THEN 'DNF'
        WHEN sr.dns = 1 THEN 'DNS'
        WHEN sr.dsq = 1 THEN 'DSQ'
        ELSE 'Finished'
    END AS status,
    CASE 
        WHEN sr.position = 1 THEN 25
        WHEN sr.position = 2 THEN 18
        WHEN sr.position = 3 THEN 15
        WHEN sr.position = 4 THEN 12
        WHEN sr.position = 5 THEN 10
        WHEN sr.position = 6 THEN 8
        WHEN sr.position = 7 THEN 6
        WHEN sr.position = 8 THEN 4
        WHEN sr.position = 9 THEN 2
        WHEN sr.position = 10 THEN 1
        ELSE 0
    END AS points
FROM SessionResult sr
INNER JOIN Session s ON sr.session_id = s.session_id
INNER JOIN Event e ON s.event_id = e.event_id
INNER JOIN Driver d ON sr.driver_id = d.driver_id
LEFT JOIN Contract c 
    ON d.driver_id = c.driver_id 
    AND s.year = (SELECT year FROM Season WHERE season_id = c.season_id)
LEFT JOIN Team t ON c.team_id = t.team_id
WHERE s.session_type = 'Race';
GO


