CREATE VIEW dbo.vw_FastestLaps AS
WITH RankedLaps AS (
    SELECT 
        e.gp_name,
        s.session_name,
        d.full_name AS driver_name,
        d.code AS driver_code,
        l.lap_number,
        l.lap_duration,
        ROW_NUMBER() OVER (PARTITION BY s.session_id ORDER BY l.lap_duration) AS lap_rank
    FROM Lap l
    INNER JOIN Session s ON l.session_id = s.session_id
    INNER JOIN Event e ON s.event_id = e.event_id
    INNER JOIN Driver d ON l.driver_id = d.driver_id
    WHERE l.lap_duration IS NOT NULL 
      AND l.is_pit_out_lap = 0
      AND l.lap_duration > 0
)
SELECT 
    gp_name,
    session_name,
    driver_name,
    driver_code,
    lap_number,
    lap_duration AS fastest_lap_time
FROM RankedLaps
WHERE lap_rank = 1;
