-- Get Driver Performance Summary
IF OBJECT_ID('dbo.sp_GetDriverPerformance', 'P') IS NOT NULL DROP PROCEDURE dbo.sp_GetDriverPerformance;
GO
CREATE PROCEDURE dbo.sp_GetDriverPerformance
    @DriverNumber INT,
    @Year INT = NULL
AS
BEGIN
    SET NOCOUNT ON;

    SELECT 
        d.full_name,
        d.code,
        s.year,
        COUNT(DISTINCT sr.session_id) AS races_entered,
        SUM(CASE WHEN sr.position = 1 THEN 1 ELSE 0 END) AS wins,
        SUM(CASE WHEN sr.position <= 3 THEN 1 ELSE 0 END) AS podiums,
        SUM(CASE WHEN sr.position <= 10 AND sr.dnf = 0 THEN 
            CASE 
                WHEN sr.position = 1 THEN 25
                WHEN sr.position = 2 THEN 18
                WHEN sr.position = 3 THEN 15
                WHEN sr.position = 4 THEN 12
                WHEN sr.position = 5 THEN 10
                WHEN sr.position = 6 THEN 8
                WHEN sr.position = 7 THEN 6
                WHEN sr.position = 8 THEN 4
                WHEN sr.position = 9 THEN 2
                WHEN sr.position = 10 THEN 1
                ELSE 0
            END 
        ELSE 0 END) AS total_points,
        AVG(CASE WHEN sr.dnf = 0 THEN CAST(sr.position AS FLOAT) ELSE NULL END) AS avg_finish_position,
        SUM(CASE WHEN sr.dnf = 1 THEN 1 ELSE 0 END) AS dnf_count
    FROM Driver d
    INNER JOIN SessionResult sr ON d.driver_id = sr.driver_id
    INNER JOIN Session s ON sr.session_id = s.session_id
    WHERE d.driver_number = @DriverNumber
      AND s.session_type = 'Race'
      AND (@Year IS NULL OR s.year = @Year)
    GROUP BY d.full_name, d.code, s.year
    ORDER BY s.year DESC;
END;
GO

/* =============================================
Get head to head
============================================= */

IF OBJECT_ID('dbo.sp_GetHeadToHead', 'P') IS NOT NULL 
    DROP PROCEDURE dbo.sp_GetHeadToHead;
GO

CREATE PROCEDURE dbo.sp_GetHeadToHead
    @Driver1Number INT,
    @Driver2Number INT,
    @Year INT = NULL
AS
BEGIN
    SET NOCOUNT ON;
    
    IF OBJECT_ID('tempdb..#D1') IS NOT NULL DROP TABLE #D1;

    SELECT 
        s.session_id,
        e.gp_name,
        sr.position AS driver1_position,
        sr.dnf AS driver1_dnf
    INTO #D1
    FROM SessionResult sr
    JOIN Session s ON sr.session_id = s.session_id
    JOIN Event e ON s.event_id = e.event_id
    JOIN Driver d ON sr.driver_id = d.driver_id
    WHERE d.driver_number = @Driver1Number
      AND s.session_type = 'Race'
      AND (@Year IS NULL OR s.year = @Year);

    IF OBJECT_ID('tempdb..#D2') IS NOT NULL DROP TABLE #D2;

    SELECT 
        s.session_id,
        sr.position AS driver2_position,
        sr.dnf AS driver2_dnf
    INTO #D2
    FROM SessionResult sr
    JOIN Session s ON sr.session_id = s.session_id
    JOIN Driver d ON sr.driver_id = d.driver_id
    WHERE d.driver_number = @Driver2Number
      AND s.session_type = 'Race'
      AND (@Year IS NULL OR s.year = @Year);

    SELECT 
        d1.gp_name,
        d1.driver1_position,
        d2.driver2_position,
        CASE 
            WHEN d1.driver1_dnf = 1 AND d2.driver2_dnf = 1 THEN 'Both DNF'
            WHEN d1.driver1_dnf = 1 THEN 'Driver 2 Wins'
            WHEN d2.driver2_dnf = 1 THEN 'Driver 1 Wins'
            WHEN d1.driver1_position < d2.driver2_position THEN 'Driver 1 Wins'
            WHEN d2.driver2_position < d1.driver1_position THEN 'Driver 2 Wins'
            ELSE 'Tie'
        END AS result
    FROM #D1 d1
    JOIN #D2 d2 ON d1.session_id = d2.session_id
    ORDER BY d1.gp_name;

    SELECT 
        (SELECT full_name FROM Driver WHERE driver_number = @Driver1Number) AS driver1_name,
        (SELECT full_name FROM Driver WHERE driver_number = @Driver2Number) AS driver2_name,
        SUM(
            CASE 
                WHEN d1.driver1_dnf = 0 AND d2.driver2_dnf = 0 
                     AND d1.driver1_position < d2.driver2_position THEN 1
                WHEN d1.driver1_dnf = 0 AND d2.driver2_dnf = 1 THEN 1
                ELSE 0
            END
        ) AS driver1_wins,
        SUM(
            CASE 
                WHEN d1.driver1_dnf = 0 AND d2.driver2_dnf = 0 
                     AND d2.driver2_position < d1.driver1_position THEN 1
                WHEN d2.driver2_dnf = 0 AND d1.driver1_dnf = 1 THEN 1
                ELSE 0
            END
        ) AS driver2_wins,
        COUNT(*) AS total_races
    FROM #D1 d1
    JOIN #D2 d2 ON d1.session_id = d2.session_id;

END;
GO


-- Get Circuit Statistics
IF OBJECT_ID('dbo.sp_GetCircuitStats', 'P') IS NOT NULL DROP PROCEDURE dbo.sp_GetCircuitStats;
GO
CREATE PROCEDURE dbo.sp_GetCircuitStats
    @CircuitName VARCHAR(120)
AS
BEGIN
    SET NOCOUNT ON;

    -- Most wins at circuit
    SELECT TOP 5
        d.full_name,
        d.code,
        COUNT(*) AS wins
    FROM SessionResult sr
    INNER JOIN Session s ON sr.session_id = s.session_id
    INNER JOIN Event e ON s.event_id = e.event_id
    INNER JOIN Driver d ON sr.driver_id = d.driver_id
    WHERE e.circuit_short_name LIKE '%' + @CircuitName + '%'
      AND s.session_type = 'Race'
      AND sr.position = 1
    GROUP BY d.full_name, d.code
    ORDER BY wins DESC;

    -- Fastest lap at circuit
    SELECT TOP 1
        d.full_name,
        d.code,
        e.gp_name,
        s.year,
        MIN(l.lap_duration) AS fastest_lap
    FROM Lap l
    INNER JOIN Session s ON l.session_id = s.session_id
    INNER JOIN Event e ON s.event_id = e.event_id
    INNER JOIN Driver d ON l.driver_id = d.driver_id
    WHERE e.circuit_short_name LIKE '%' + @CircuitName + '%'
      AND l.lap_duration IS NOT NULL
      AND l.is_pit_out_lap = 0
      AND l.lap_duration > 0
    GROUP BY d.full_name, d.code, e.gp_name, s.year
    ORDER BY fastest_lap;
END;
GO

